package apple_test

import (
	"github.com/smartwalle/apple"
	"testing"
)

func TestVerifyReceipt(t *testing.T) {
	var summary, info, err = apple.VerifyReceipt("460001400577115", "")
	if err != nil {
		t.Fatal(err)
	}
	t.Log(summary)
	if info != nil {
		t.Log(summary.Environment, info.TransactionId, info.ProductId)
	}
}
